package operators;

public class unary_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 13;
		System.out.println("pre-increament" + ++i); //14
		System.out.println("post-increament" + i++); //14
		System.out.println("i value " + i); //15
		System.out.println("pre-decreament " + --i); //14
		System.out.println("post-decreament " + i--); //14
		System.out.println("i value is " + i);  //13

	}

}
